// Inorder iterative solution
class Solution {
public:
    int kthSmallest(TreeNode* root, int k) {
        stack<TreeNode*> s;

        while(!s.empty() || root != NULL){
            while(root != NULL){
                s.push(root);
                root = root->left;
            }
            root = s.top();
            s.pop();
            if(--k == 0)
                break;
            root = root->right;
        }
        return root->val;
    }
};

