        if(root == NULL)
            return;

        inorder(root->left, k, ans);
        if(--k == 0)
            ans = root->val;
        inorder(root->right, k, ans);
    }

    int kthSmallest(TreeNode *root, int k){
        int ans = 0;
        inorder(root, k, ans);
        return ans;
    }
};
