            if (node is not null){
                foreach(var n in Inorder(node.left)){
                    yield return n;
                }
                // Trace.WriteLine(node.val); // Output: 1, 2, .. k
                yield return node.val;
                foreach(var n in Inorder(node.right)){
                    yield return n;
                }
            }


        }
    }
}
