        if(!root)
            return -1;

        int left = kthSmallest(root->left, k);
        if(left!=-1)
            return left;

        if(--k==0)
            return root->val;

        int right = kthSmallest(root->right, k);
        if(right!=-1)
            return right;
        return -1;
    }
};
