class Solution {
public:
    int kthSmallest(TreeNode* root, int k) {

        // Since BST is in increasing order when pre-order traversaled 
        // do iterative inorder traversal till k elements are found

        if(!root) return -1;

        stack<TreeNode*> s;

        while(1){ // since k is in the range of 1 - #elements in the BST

            while(root){
                s.push(root);
                root = root->left;
            }

            root = s.top();
            s.pop();
            k--;
            if(k == 0){
                return root->val;
            }

            root = root->right;            
        }

        return -1;
    }
};
```

