func Traverse(c chan int, root *TreeNode) {
    if root == nil {
        return
    }
    Traverse(c, root.Left)
    c <- root.Val
    Traverse(c, root.Right)
}

func kthSmallest(root *TreeNode, k int) int {
    c := make(chan int)
    go func() {
        Traverse(c, root)
        close(c)
    }()
    x, ok := <-c
    for ; k > 1 && ok; x, ok = <-c {
        k--
    }
    return x
}
