class BSTIterator {

    Stack<TreeNode> s;

    public BSTIterator(TreeNode root) {
       this.s=new Stack<TreeNode>(); 

       pushAll(root);

    }
    public void pushAll(TreeNode node){
        while(node!=null){
            s.push(node);
            node=node.left;
        }
    }

    public int next() {

        if(s.size()==0)return -1;
        TreeNode root=s.pop();
        int v=root.val;
        if(root.right!=null){
            pushAll(root.right);
        }
        return v;
    }

    public boolean hasNext() {
        if(s.size()>0)return true;
        return false;
    }
}

class Solution {
    public int kthSmallest(TreeNode root, int k) {

        if(root==null)return -1;

      BSTIterator itr=new BSTIterator(root);
        int ans=-1;
        while( itr.hasNext() && k-->0){
            ans=itr.next();
        }
        return ans;
    }
}
