class Solution {
    bool foundKthSmallest(TreeNode* root, int &currCount, const int k, int &result) {
        if (root == nullptr)
            return false;

        if (foundKthSmallest(root->left, currCount, k, result))
            return true;

        currCount++;
        if (currCount == k)
        {
            result = root->val;
            return true;
        }

        if(foundKthSmallest(root->right, currCount, k, result))
            return true;

        return false;
    }
public:
    int kthSmallest(TreeNode* root, int k) {
        int res = INT_MAX;
        int currCount = 0;
        foundKthSmallest(root, currCount, k, res);
