                reach += (reach + 1);
                ++patch;
                if (reach >= n) return patch;
            }
            reach += nums[i];
            if (reach >= n) return patch;
            ++i;
        }
        while (reach < n){
            reach += (reach + 1);
            ++patch;
        } 
