    fun minPatches(nums: IntArray, n: Int): Int {
        var currentMax = 0L
        var currentNumIndex = 0
        var res = 0
        while (currentMax < n) {
            if (currentNumIndex < nums.size && nums[currentNumIndex] <= currentMax + 1) {
                currentMax += nums[currentNumIndex]
                ++currentNumIndex
            } else {
                currentMax += currentMax + 1 
                ++res
            }
        }
        return res
    }
}
