class Solution {
public:
    int minPatches(vector<int>& a, int n) {
         int appends = 0;
         multiset<long> mset;
         for(int i=0; i<a.size(); i++) mset.insert(a[i]);
         long long sm  = 1;
         while(sm<n) {
             sm = 1;
             for(long x : mset) {
                  if(sm<x) {
                      break;
                  }else {
                      sm+=x;
                  }
             }
             // smallest number(Sm) that can't be created between 1..N from the elements in the multiset!
             if(sm<=n) { 
                 appends++;      
                 mset.insert(sm);  // append that number(Sm) in the multiset!
             }
             // now we can create sum from 1...number(Sm) from the elements of multiset!
         }
        return appends;
    }
};

