    int minPatches(vector<int>& nums, int n) {        
        unsigned int maxv = 0; // 1 ~ maxv are available
        int cnt = 0; // minimum count
        nums.push_back(n);
        for (int num : nums) {
            // if maxv + 1 < num, it can not make maxv + 1 ~ num - 1
            // After maxv + maxv+1, 1 ~ 2*maxv +1 are available
            while(maxv + 1 < num) { 
                cnt += 1;                
                maxv += maxv+1;
                if(maxv >= n) return cnt;                
            }            
            maxv += num; // maxv + num are available                  
            if(maxv >= n){
                return cnt;                            
            }
        }

        return cnt;
    }
};
