package main

import (
	"container/heap"
	"sort"
)

type Solution struct{}

// Depth-first search function to find the itinerary
func (s Solution) dfs(adj map[string]*PriorityQueue, result *[]string, s string) {
	// While there are destinations connected to the current airport
	for adj[s].Len() > 0 {
		// Get the next destination
		v := heap.Pop(adj[s]).(string)
		// Recursively explore this destination
		s.dfs(adj, result, v)
	}
	// Add the current airport to the result
	*result = append(*result, s)
}

// Main function to find the itinerary
func (s Solution) findItinerary(tickets [][]string) []string {
	// Create an adjacency list to represent the flights
	adj := make(map[string]*PriorityQueue)
	for _, t := range tickets {
		src, dest := t[0], t[1]
		if _, ok := adj[src]; !ok {
			adj[src] = &PriorityQueue{}
			heap.Init(adj[src])
		}
		heap.Push(adj[src], dest)
	}

	// Initialize the result slice
	var result []string
	// Start the depth-first search from JFK (John F. Kennedy International Airport)
	s.dfs(adj, &result, "JFK")
	// Reverse the result to get the correct itinerary
	sort.Sort(sort.Reverse(sort.StringSlice(result)))
	// Return the itinerary
	return result
}

// PriorityQueue is a simple priority queue implemented using a slice
type PriorityQueue []string

func (pq PriorityQueue) Len() int           { return len(pq) }
func (pq PriorityQueue) Less(i, j int) bool { return pq[i] < pq[j] }
func (pq PriorityQueue) Swap(i, j int)      { pq[i], pq[j] = pq[j], pq[i] }

func (pq *PriorityQueue) Push(x interface{}) {
	*pq = append(*pq, x.(string))
}

func (pq *PriorityQueue) Pop() interface{} {
	old := *pq
	n := len(old)
	item := old[n-1]
	*pq = old[0 : n-1]
	return item
}

