        for(auto& temp : graph){
            sort(temp.second.rbegin(), temp.second.rend());
        }

        vector<string> itinerary;
        string jfk = "JFK";
        dfs(jfk, graph, itinerary);

        reverse(itinerary.begin(), itinerary.end());

        return itinerary;

    }
};
