                G[A] = deque(G[A])
            return G

        def dfs(G, S):
            trip.append(S)
            if len(trip) == length:
                return True
            if S in G:
                n, i = len(G[S]), 0
                while i < n:
                    A = G[S].pop()
                    if dfs(G, A):
                        return True
                    G[S].appendleft(A)
                    i += 1
            trip.pop()
            return False


        G = build_graph(tickets)
        trip, length = [], len(tickets) + 1
        dfs(G, "JFK")
        return trip

