    vector<string> findItinerary(vector<vector<string>>& tickets) {
        int n = tickets.size();
        sort(tickets.begin(), tickets.end());
        unordered_map<string,vector<string>> m;

        for(int i = 0; i < n; i++) {
            m[tickets[i][0]].push_back(tickets[i][1]);
        }

        vector<string> res;
        res.push_back("JFK");
        bool x = dfs(m, res, "JFK", n);
        return res;
    }
};
