class Solution {
public:
    vector<string> findItinerary(vector<vector<string>>& tickets) {
        unordered_map<string, multimap<string, bool>> graph;
        for (vector<string> data : tickets)
            graph[data[0]].insert({data[1], false});

        vector<string> out;

        dfs(out, {"JFK", 0}, graph);

        reverse(out.begin(), out.end());
        return out;
    }

    void dfs(vector<string>& out, pair<string, bool> node, unordered_map<string, multimap<string, bool>>& graph) {  
        for (auto& data : graph[node.first]) {
            if (!data.second) {
                data.second = true;
                dfs(out, data, graph);
            }
        }
        out.push_back(node.first);
    }
};
