            else {
                map.get(ticket.get(0)).add(ticket.get(1));
            }
        }  
        visit("JFK");
        return route;
    }

    public void visit(String airport) {
        while(map.containsKey(airport) && !map.get(airport).isEmpty()) {
            PriorityQueue<String> dests = map.get(airport);
            String dest = dests.poll();
            visit(dest);
        }
        route.add(0,airport); 
    }
}

