class Solution {
public:
    vector<string> findItinerary(vector<pair<string, string>> tickets) {
        unordered_map < string, priority_queue<string, vector<string>, greater<string>>> f2tos;
        for (auto ft : tickets) {
            f2tos[ft.first].push(ft.second);
        }

        vector<string> result;
        if (tickets.empty() || f2tos.find("JFK") == f2tos.end()) {
            return result;
        }

        string start = "JFK";
        while (!f2tos.empty()) {
            auto pq2 = f2tos[start];
            result.push_back(start);
            auto next = pq2.top();
            pq2.pop();
            if (pq2.empty()) {
                f2tos.erase(start);
            }

            start = next;
        }

        result.push_back(start);

        return result;
    }
};
