class Solution {
    HashMap<String, PriorityQueue<String>>  graph = new HashMap<>();
    LinkedList<String> result  = new LinkedList<>();

    public List<String> findItinerary(List<List<String>> tickets) {
        this.buildGraph(tickets);
        this.dfs("JFK");
        return this.result;
    }

    private void dfs(String origin) {
        if (this.graph.containsKey(origin)) {
          PriorityQueue<String> set = this.graph.get(origin);
          while (!set.isEmpty()) {
            this.dfs(set.poll());
          }
        }
        this.result.offerFirst(origin);
    }

    private void buildGraph(List<List<String>> tickets) {
        for(List<String> list: tickets) {
            PriorityQueue<String> set = graph.getOrDefault(list.get(0), new PriorityQueue<String>());
            set.add(list.get(1));
            graph.put(list.get(0), set);
        }
    }
}
