def findItinerary(tickets):
    graph = {}

    tickets.sort(key=lambda x: x[1])
    expected = len(tickets) + 1

    for index in range(len(tickets)):
        ticket = tickets[index]
        frm = ticket[0]
        to = ticket[1]

        if frm not in graph:
            graph[frm] = []
        if to not in graph:
            graph[to] = []
        graph[frm].append((to, '{}{}'.format(index,to)))

    def dfs(node, path, visited):

        if len(path) == expected:

            return path

        for info in graph[node]:
            neighbor, _id = info

            edge = (node, _id)
            if edge not in visited:
                visited.add(edge)
                path.append(neighbor)
                potential = dfs(neighbor, path, visited)

                if potential:
                    return potential

                visited.remove(edge)
                path.pop()

        return None

    return dfs("JFK", ["JFK"], set())

