```csharp
public class Solution {
    public IList<string> FindItinerary(IList<IList<string>> tickets) {
        var map = new Dictionary<string, List<string>>();
        foreach (var ticket in tickets) {
            if (!map.TryGetValue(ticket[0], out var adj))  {
                adj = new List<string>();
                map.Add(ticket[0], adj);
            }
            adj.Add(ticket[1]);
        }

        foreach (var adj in map.Values) {
            adj.Sort(Comparer<string>.Create((a, b) => string.Compare(b, a)));
        }

        var ans = new Stack<string>();
        Visit(map, "JFK", ans);
        return ans.ToList();
    }

    private void Visit(Dictionary<string, List<string>> map, string src, Stack<string> ans) {
        if (map.TryGetValue(src, out var adj)) {
            while (adj.Count > 0) {
                var next = adj.Last();
                adj.RemoveAt(adj.Count - 1);
                Visit(map, next, ans);
            }
        }
        ans.Push(src);
    }
}
