class Solution {
public:
    void dfs(unordered_map<string, vector<pair<string,int>>>&hash, int e, string index, vector<string>&ans, int count, vector<string>&fin){
        ans.push_back(index);
        if(count == e){
            fin = ans;
            return;
        }
        //vector<pair<string, int>> t = hash[index];
        for(int i = 0 ; i  < hash[index].size(); i++){
            if(!hash[index][i].second){
                hash[index][i].second = 1;
                dfs(hash,e,hash[index][i].first, ans, count+1,fin);
                if(fin == ans){
                    return;
                }
                hash[index][i].second = 0;
            }
        }
        ans.pop_back();
        return;
    }

    vector<string> findItinerary(vector<vector<string>>& A) {
        int n = A.size();
        unordered_map<string, vector<pair<string,int>>>hash;
        for(int i = 0 ; i  < n ; i++){
            hash[A[i][0]].push_back({A[i][1], 0});
        }
        unordered_map<string, vector<pair<string,int>>>:: iterator it;
        for(it = hash.begin(); it !=hash.end(); it++){
            sort(it->second.begin(), it->second.end());
        }
        vector<string>ans;
        string src = "JFK";
        vector<string>fin;
        dfs(hash,n,src,ans,0,fin);
        return fin;
    }
};

