class Solution:
    def findItinerary(self, tickets: List[List[str]]) -> List[str]:

        length= len(tickets)+1
        graph= collections.defaultdict(list)

        for ticket in tickets:
            graph[ticket[0]].append(ticket[1])

        for g in graph:
            graph[g].sort()

        self.answer=[]
        def dfs(node, result):
            if len(result)== length:
                self.answer= result
                return True
            res= False
            for i,n in enumerate(graph[node]):
                if n!= '#':
                    city= n
                    graph[node][i]= '#'
                    result.append(city)
                    res= dfs(city, result)
                    if res:
                        return res
                    graph[node][i]= city
                    result.pop()
            return res

        dfs('JFK', ['JFK'])

        return self.answer




