class Solution {
    typedef unordered_map<string, priority_queue<string, vector<string>, greater<string>>> GraphType;
public:
    vector<string> findItinerary(vector<vector<string>>& tickets) {

        GraphType graph;
        vector<string> res;

        for (auto t : tickets)
            graph[t[0]].push(t[1]);

        dfs("JFK", graph, res);
        reverse(res.begin(), res.end());
        return res;
    }

    void dfs(string str, GraphType& graph, vector<string>& res) {

        auto & v = graph[str];
        while (!v.empty()) {

            auto cur = v.top();
            v.pop();
            dfs(cur, graph, res);
        }

        res.push_back(str);
     }
};

