        graph = collections.defaultdict(collections.deque)
        for i, j in sorted(tickets):
            graph[i].append(j)
        route, stack = [], ['JFK']
        while stack:
            while graph[stack[-1]]:
                stack.append(graph[stack[-1]].popleft())
            route += stack.pop(),
        return route[::-1]

