from collections import defaultdict, deque
class Solution:
    def findItinerary(self, tickets: List[List[str]]) -> List[str]:

        def make_graph():
            for depart, arrival in tickets:
                tickets_graph[depart].append(arrival)
            return tickets_graph

        def dfs_helper(curr_city = "JFK", temp_itenary = []):

            if len(temp_itenary) == len_itenary:
                return temp_itenary + [curr_city]

            if curr_city not in tickets_graph: # we have reached a deadend
                return None

            for next_city in sorted(tickets_graph[curr_city]):
                tickets_graph[curr_city].remove(next_city) #to avoid indefinite cycles
                solution = dfs_helper(next_city, temp_itenary+[curr_city])
                if solution: return solution #return as soon as path found
                tickets_graph[curr_city].append(next_city)

        tickets_graph = defaultdict(list)
        make_graph()
        len_itenary = len(tickets)
        return dfs_helper()
