class Solution {

    Map<String ,List<String>> h = new HashMap<>();

    List<String > ans = new ArrayList<>();
    int totalticket =0;

    public List<String> findItinerary(List<List<String>> tickets) {


        int n = tickets.size();

        totalticket = n+1;

        for(int i=0;i<n;i++){

            String u = tickets.get(i).get(0);
            String v = tickets.get(i).get(1);

            if(h.containsKey(u)){

                h.get(u).add(v);
            }else{

                h.put(u,new ArrayList<>());
                h.get(u).add(v);
            }

        }

        for(List<String> i : h.values()){

            Collections.sort(i);
            System.out.println(i);
        }


        dfs("JFK",new ArrayList<String>());

        return ans;

    }

    public boolean dfs(String from, List<String> path){

        path.add(from);

        if(path.size()==totalticket){
            ans.addAll(path);
            return true;
        }

        List<String> neigh = h.get(from);

        if(neigh==null){

            path.remove(from);
            return false;
        }


        for(int i=0;i<neigh.size();i++){

            String to = neigh.get(i);
            neigh.remove(i);

            if(dfs(to,path)){
                return true;
            }

            neigh.add(i,to);

        }

        path.remove(path.size()-1);
        return false;
    }
}
