public class Solution {
    public IList<string> FindItinerary(IList<IList<string>> tickets) {
        Dictionary<string, List<string>> graph = new Dictionary<string, List<string>>();
        List<string> itinerary = new List<string>();

        // Build the graph from the given tickets
        foreach (var ticket in tickets) {
            string from = ticket[0];
            string to = ticket[1];
            if (!graph.ContainsKey(from)) {
                graph[from] = new List<string>();
            }
            graph[from].Add(to);
        }

        // Sort the destinations in lexical order
        foreach (var key in graph.Keys) {
            graph[key].Sort();
        }

        // Start the DFS from JFK
        DFS(graph, "JFK", itinerary);

        // Reverse the itinerary since DFS appends in reverse order
        itinerary.Reverse();
        return itinerary;
    }

    private void DFS(Dictionary<string, List<string>> graph, string airport, List<string> itinerary) {
        if (graph.ContainsKey(airport) && graph[airport].Count > 0) {
            List<string> destinations = graph[airport];
            while (destinations.Count > 0) {
                string nextAirport = destinations[0];
                destinations.RemoveAt(0);
                DFS(graph, nextAirport, itinerary);
            }
        }
        itinerary.Add(airport);
    }
}
