#include <bits/stdc++.h>
#define mp unordered_map<string, priority_queue<string, vector<string>, greater<string>>>

class Solution {
public:

    void dfs(string start, mp& adj, stack<string>& stk ){
        auto& minPQ= adj[start];

        while(!minPQ.empty()){
            string baccha= minPQ.top(); // lexixographical chota baccha
            minPQ.pop(); // after using edge, delete it so that we can ever use it again

            dfs(baccha, adj, stk);
        }

        stk.push(start);
        return;
    }

    vector<string> findItinerary(vector<vector<string>>& ticket) {
        mp adj;

        for(int i=0; i<ticket.size(); i++){
            string u= ticket[i][0];  string v= ticket[i][1];
            adj[u].push(v);
        }

        stack<string> stk;
        // no need of visited array. As we can visit node multiple time. We just need to visit every edge only once
        dfs("JFK", adj, stk); 

        vector<string> ans; 
        while(!stk.empty()){  ans.push_back(stk.top());  stk.pop();  }
        return ans;
    }
};
