class Solution:
    def isValidSudoku(self, board: List[List[str]]) -> bool:

        # Given an arr of row/col/sub,
        # check if there are duplicates.
        def isValid(arr):
            s = ''.join(arr).replace('.','')
            return len(s) == len(set(s))

        # Check each row in the board
        def checkRow():
            for row in board:
                if not isValid(row):
                    return False
            return True

        # Check each col in the board,
        # To access each col, we first unpack the board into sperate lists using *
        # We then zip these rows together into columns.
        def checkCol():
            for col in zip(*board):
                if not isValid(col):
                    return False
            return True

        # To get each sub-box, we first get the top-left indices of each sub-box,
        # We then go 3 steps on each row and 3 steps on each col to construct the box.
        def checkSub():
            for r in range(0,9,3):
                for c in range(0,9,3):
                    sub = [board[r+dr][c+dc] for dr in range(3) for dc in range(3)]
                    if not isValid(sub):
                        return False
            return True

        # In order to be a valid Sudoku, all row, col, and sub-box need to be valid
        return checkRow() and checkCol() and checkSub()
```
```python
class Solution:
    def isValidSudoku(self, board: List[List[str]]) -> bool:

        # Row, Col, and subBox will each contain 9 sets to keep track of the visited element.
        # Using 0-8 as the id for each row, col, and sub-box.
        # At any time if the same number is visited twice, return False. 
        Row = defaultdict(set)
        Col = defaultdict(set)
        subBox = defaultdict(set)

        for r in range(9):
            for c in range(9):

                cur = board[r][c]
                if cur=='.':
                    continue

                # r is the id for Row 
                if cur in Row[r]:
                    return False
                Row[r].add(cur)

                # c is the id for Col
                if cur in Col[c]:
                    return False
                Col[c].add(cur)

                # sub-box id is a little bit tricky
                # r//3*3 will map the number 0-8 to 0,3,6 which is the starting id for each row.
                # c//3 will map the number 0-8 to 0,1,2, which is the column.
                # So r//3*3+c//3 is corresponding to sub-box id as shown
                                                             ----- ----- -----
                boxId = r//3*3+c//3                         |  0  |  1  |  2  |
                if cur in subBox[boxId]:                     ----- ----- -----
                    return False                            |  3  |  4  |  5  |
                subBox[boxId].add(cur)                       ----- ----- -----
                                                            |  6  |  7  |  8  |
                                                             ----- ----- -----
        # We passed all the tests, return True
        return True
