            if(board[row][i] == num)
                return false;
        }

        //For col checking
        for(int i=row+1;i<9;i++){
            if(board[i][col] == num)
                return false;
        }

        //for internal square
        int x = row - row%3, y = col-col%3;
        for(int i=x; i<x+3; i++){
            for(int j=y; j<y+3; j++){
                if(board[i][j]==num && !(i==row && j==col))
                    return false;
            }
        }   
        return true;
    }   
}



