class Solution {
public:
    bool isValidSudoku(vector<vector<char>>& board) {
        int n = board.size();
        unordered_map<string, int> seen;

        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                int element = board[i][j];
                // skip if the cell is not filled
                if(element == '.')
                    continue;

                // string for each element found at a row to store in hashmap
                string rowFind = to_string(element) + "found at row" + to_string(i);

                // string for each element found at a column to store in hashmap
                string colFind = to_string(element) + "found at column" + to_string(j);

                // string for each element found at a sub-box to store in hashmap
                string boxFind = to_string(element) + "found at box" + to_string(i/3) + '-' + to_string(j/3);

                // checking if those are already inside hashmap, if not we'll be putting em into hashmap
                if(seen.find(rowFind) != seen.end() ||
                   seen.find(colFind) != seen.end() ||
                   seen.find(boxFind) != seen.end()){
                    return false;
                }
                else{
                    seen[rowFind]++;
                    seen[colFind]++;
                    seen[boxFind]++;
                }
            }
        }
        return true;
    }
};
