            if(board[j][i] != '.') {
                if(colMap[board[j][i]]) return false;
                colMap[board[j][i]] = 1;
            }
            if(box!= '.'){
                if(boxMap[box]) return false;
                boxMap[box] = 1;
            }
        }
    }
    return true;
};
