class Solution {
    bool isValid(vector<vector<char>> &board, char ch, int row, int col) {
        for (int i = 0; i < 9; i++) {
            if (board[i][col] == ch) 
                return false;
            if (board[row][i] == ch) 
                return false;
            if (board[3 * (row / 3) + i / 3][3 * (col / 3) + i % 3] == ch) 
                return false;       
        }
        return true;
    }
public:
    bool isValidSudoku(vector<vector<char>>& board) {
        int n = board.size();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (board[i][j] != '.') {
                    char ch = board[i][j];
                    if (!isValid(board, ch, i, j)) 
                        return false;
                }
            }
        }
        return true;
    }
};
