```
func singleNumber(_ nums: [Int]) -> Int {
    var numsSeen = Set<Int>()

    for num in nums {
        // the insert operation returns a tuple, the first value of which is a bool,
        // indicating whether the insertion is successful
        if !numsSeen.insert(num).0 {
            numsSeen.remove(num)
        }
    }

    // it's ok to force unwrap as a valid answer is guaranteed
    return numsSeen.first!
}
