```go
func singleNumber(nums []int) int {
  m := map[int]struct{}{}
  for _, num := range nums {
    if _, found := m[num]; found {
      delete(m, num)
    } else {
      m[num] = struct{}{}
    }
  }
  for k := range m {
    return k
  }
  return -1
}
