import java.util.Arrays;
class Solution {
    public int singleNumber(int[] nums) {
        int n = nums.length;
        Arrays.sort(nums);
        int ans = nums[0];
        for (int i = 0; i < n-1; i+=2){
            if (!(nums[i] == nums[i+1])){
                ans = nums[i];
                return nums[i];
            }
            else{
                ans = nums[n-1];
                return nums[n-1];
            }
        }
        return ans;
    }
}

