class Solution {
public:
    bool find(TreeNode* root, TreeNode* x){
        if(root == NULL)
            return false;
        if(root == x)
            return true;

        bool left = find(root->left, x);
        bool right = find(root->right, x);

        if(left == true or right == true)
            return true;
        else
            return false;
    }

    TreeNode* LCA(TreeNode* root, TreeNode* p, TreeNode* q){
        if(root == NULL || root == p || root == q)
            return root;

        TreeNode* left = LCA(root->left, p, q);
        TreeNode* right = LCA(root->right, p, q);

        if(left == NULL)
            return right;
        if(right == NULL)
            return left;
        else // both left and right are non NULL
            return root;
    }

    TreeNode* lowestCommonAncestor(TreeNode* root, TreeNode* p, TreeNode* q) {

        bool first = find(root, p); // finding if p exist in binary tree or not
        bool second = find(root, q); // finding if q exist in binary tree or not

        if(first == false or second == false) // if one of p or q not exist in binary tree
            return NULL;

        return LCA(root, p, q);    
    }
};
