class Solution {
public:
    TreeNode* lowestCommonAncestor(TreeNode* root, TreeNode* p, TreeNode* q) {
        std::stack<TreeNode *> txStk;
        TreeNode *prev = NULL; /* previous node visisted in Post-order traversal */
        TreeNode *curr = NULL; 
        TreeNode *parent = NULL; /* placeholder for LCA */


        int p_found = 0;
        int q_found = 0;

        if (!root) return NULL;
        if (p == q) return p;

        /* Process left child of root */
        for (TreeNode *tmp = root; tmp; tmp = tmp->left) 
            txStk.push(tmp);

        while (!txStk.empty()) {
            curr = txStk.top();

            /*
             * If prev was marked as a probable ancestor, then it would either be one of
             * (p,q) or their ancestor. Now that prev has been visited and we found only
             * one of (p, q), we'll go to its ancestor to find next. Update ancestor while we do that.
             */
            if (parent && prev == parent) parent = curr;


            /*
             * 1. Left tree has already been processed. If last node we
             * processed was not right child, process right child
             */
            if (curr->right && prev != curr->right) {
                /* 2. Process left chlid of this node */
                for (TreeNode *tmp = curr->right; tmp; tmp = tmp->left)
                    txStk.push(tmp);
                continue;
            }

            txStk.pop();

            /* 
             * Because we're doing a ordered traversal, the left most node will be found first
             * or the child node if one is ancestor of other. When we've found second node,
             * the ancestor is already captured correctly in parent.
             */
            if (curr == p) {
                if (q_found) return parent;
                p_found = 1;
                parent = curr;
            }

            if (curr == q) {
                if (p_found) return parent;
                q_found = 1;
                parent = curr;
            }

            /* Remember last node we visited in the traversal */
            prev = curr;
        }

        return NULL;
    }
};

```

