        TreeNode r = lowestCommonAncestor(root.right, p, q);

		// If both p & q were found in sub-trees of root.
		if(l!=null && r!=null) return root;

		// if only either of them was found, then return the one which is not null.  This logic covers side cases.
		return l==null ? r : l;
    }
