        d = {w: i for i, w in enumerate(words)}
        ans = []
        for i, w in enumerate(words):
            # no divide, two cases
            if w[::-1] in d and d[w[::-1]] != i:
                ans.append([i, d[w[::-1]]])
            if w != '' and w[::-1] == w and '' in d:
                ans.append([i, d['']])
