class Solution {
   private static class TrieNode {
    TrieNode[] next;
    int index;
    List<Integer> list;

    TrieNode() {
    	next = new TrieNode[26];
    	index = -1;
    	list = new ArrayList<>();
    }
}

public List<List<Integer>> palindromePairs(String[] words) {
    List<List<Integer>> res = new ArrayList<>();

    TrieNode root = new TrieNode();

    for (int i = 0; i < words.length; i++) {
        addWord(root, words[i], i);
    }

    for (int i = 0; i < words.length; i++) {
        search(words, i, root, res);
    }

    return res;
}

private void addWord(TrieNode root, String word, int index) {
  boolean[] idx = getRightPalindrome(word);
    for (int i = word.length() - 1; i >= 0; i--) {
        int j = word.charAt(i) - 'a';

        if (root.next[j] == null) {
            root.next[j] = new TrieNode();
        }

        if (idx[i]) {
            root.list.add(index);
        }

        root = root.next[j];
    }

    root.list.add(index);
    root.index = index;
}

private void search(String[] words, int i, TrieNode root, List<List<Integer>> res) {
    boolean[] idx = getLeftPalindrome(words[i]);
  int l = words[i].length();
    for (int j = 0; j < words[i].length(); j++) {	
    	if (root.index >= 0 && root.index != i && idx[l-1-j]) {
    	    res.add(Arrays.asList(i, root.index));
    	}

    	root = root.next[words[i].charAt(j) - 'a'];
      	if (root == null) return;
    }

    for (int j : root.list) {
    	if (i == j) continue;
    	res.add(Arrays.asList(i, j));
    }
}

private boolean isPalindrome(String word, int i, int j) {
    while (i < j) {
    	if (word.charAt(i++) != word.charAt(j--)) return false;
    }

    return true;
}
private boolean[] getRightPalindrome(String word) {
    boolean[] idx = new boolean[word.length()];
    String wordRev = new StringBuilder(word).reverse().toString();
    int[] lps = KMPPreprocessing(word);

      int l = word.length();
    int  start = 0;
    for (int j=0;j<l;j++) {
      while (start > 0 && wordRev.charAt(j) != word.charAt(start)) {
        start = lps[start-1]+1;
      }
      if (wordRev.charAt(j)==word.charAt(start) ){
        start++;
      }
    }
    start--;
    while (start >= 0) {
      idx[start] = true;
      start = lps[start];
    }
    return idx;
}

private boolean[] getLeftPalindrome(String word) {
    boolean[] idx = getRightPalindrome(new StringBuilder(word).reverse().toString());
    return idx;
}

private int[] KMPPreprocessing(String s) {
  int n = s.length();
  int[] f = new int[n];
  if (n == 0) {
    return f;
  }
  f[0] = -1;
  int start = 0;
  for (int i=1;i<n;i++){
    if (s.charAt(i) == s.charAt(f[i-1]+1) ){
      f[i] = f[i-1]+1;
    }else {
      start = f[i-1];
      while (start >= 0 && s.charAt(i) != s.charAt(start+1) ){
        start = f[start];
      }
      if (s.charAt(i) == s.charAt(start+1)) {
        f[i] = start+1;
      }else {
        f[i]= start;
      }
    }
  }
  return f;
}
}
