func (t *Trie) addWord(s string, index int) {
  idx := getRightPalindrome(s);
  l := len(s)
  for j:= l-1;j>=0;j--{
    c := int(s[j]-'a')
    if t.next[c] == nil {
      t.next[c] = &Trie{index:-1}
    }
    if idx[j] {
      t.list = append(t.list, index)
    }
    t= t.next[c]
  }
  t.list = append(t.list, index)
  t.index= index
}

func (t *Trie) searchAndAppendWordPair(s string, index int, res [][]int) [][]int {
  idx := getLeftPalindrome(s);

  l := len(s)
  for j:= 0;j<l;j++{
    if t.index != -1 && t.index != index && idx[j]/*isPalindrome(s, j, l-1)*/ {
      res = append(res, []int{index, t.index})
    }
    c := int(s[j]-'a')
    if t.next[c] == nil {
      return res
    }
    t= t.next[c]
  }
  for _, j:= range t.list {
    if index != j {
      res = append(res, []int{index, j})
    }
  }
  return res
}

