public class Solution {
    public IList<IList<int>> PalindromePairs(string[] words)
    {
        int count = words.Length;
        List<IList<int>> res = new();

        Dictionary<string, int> d = new ();
        for (int i = 0; i < words.Length; i++) d[words[i]] = i;

        for (int i = 0; i < count; i++)
        {
            int len = words[i].Length;
            string reversed = Reverse(words[i]);
            if (d.TryGetValue(reversed, out int ans1) && ans1 != i) res.Add(new List<int>{ i, ans1 });

            for (int j = 0; j < len; j++)
            {
                if (IsPalindrome(reversed, 0, len - 1 - j) && d.TryGetValue(reversed.Substring(len - j), out int ans2)) res.Add(new List<int>{ i, ans2 });
                if (IsPalindrome(reversed, j, len - 1) && d.TryGetValue(reversed.Substring(0, j), out int ans3)) res.Add(new List<int>{ ans3, i });
            }
        }

        return res;
    }

    private bool IsPalindrome(string s, int lo, int hi)
    {
        while (lo < hi)
        {
            if (s[lo++] != s[hi--]) return false;
        }

        return true;
    }

    private string Reverse(string text) {
        char[] charArray = text.ToCharArray();
        Array.Reverse(charArray);
        return new string(charArray);
    }
}
