  insertReversed(words);
  handleEmptyString(root);

  for (let i = 0; i < words.length; i++) {
    const lastPrefixNode = getLastPrefixNode(words[i], i);

    if (lastPrefixNode) traverse(lastPrefixNode, i, '');
  }

  return output;
