    class TrieNode{
        int id;
        TrieNode[] children;
        List<Integer> pos;
        boolean hasWord;
        public TrieNode(){
            this.id = -1;
            this.children = new TrieNode[26];
            this.hasWord = false;
            this.pos = new ArrayList<>();
        }
    }
    List<List<Integer>> res = new ArrayList<>();
    TrieNode root = new TrieNode();
    public List<List<Integer>> palindromePairs(String[] words) {
        //Trie
        //N * Len, N * Len^2
        if(words == null || words.length == 0)
            return res;
        for(int i = 0; i < words.length; i ++){
            insert(words[i], i);
        }
        for(int i = 0; i < words.length; i ++){
            search(words[i], i);
        }
        return res;
    }
    public void insert(String word, int index){
        TrieNode node = root;
        for(int i = word.length() - 1; i >= 0; i --){
            if(isPalin(word, 0, i)){
                node.pos.add(index);
            }
            int k = word.charAt(i) - 'a';
            if(node.children[k] == null){
                node.children[k] = new TrieNode();
            }
            node = node.children[k];
        }
        node.hasWord = true;
        node.pos.add(index);
        node.id = index;

    }


    public void search(String word, int index){
        TrieNode node = root;
        for(int i = 0; i < word.length(); i ++){
            if(node.id >= 0 && node.id != index && isPalin(word, i, word.length() - 1)){
                List<Integer> tmp = new ArrayList<>();
                tmp.add(index);
                tmp.add(node.id);
                res.add(tmp);
            }
            int k = word.charAt(i) - 'a';
            if(node.children[k] == null){
                return;
            }
            node = node.children[k];
        }
        for(int item : node.pos){
            if(item == index){
                continue;
            }
            List<Integer> tmp = new ArrayList<>();
            tmp.add(index);
            tmp.add(item);
            res.add(tmp);
        }

    }
    private boolean isPalin(String word, int start, int end){
        while(start < end){
            if(word.charAt(start) != word.charAt(end))
                return false;
            start ++;
            end --;
        }
        return true;
    }
}
