// case 2: reversed string existed
// case 3: find the pair s1, s2 that 
//         s1[cut: ] isPalindrome => reverse(s2[0:cut]) exists => (s1, s2)
//         s1[0:cut] isPalindrome => reverse(s2[cut: ]) exists => (s2, s1)

class Solution {
    public List<List<Integer>> palindromePairs(String[] words) {
        List<List<Integer>> result = new ArrayList<>();
        HashMap<String, Integer> map = new HashMap<>();
        for(int i = 0; i < words.length; i++) {
            map.put(words[i], i);
        }

        if(map.containsKey("")) {
            int blank = map.get("");
            for(int i = 0; i < words.length; i++) {
                if(isPalindrome(words[i]) && i != blank) {
                    result.add(Arrays.asList(blank, i));
                    result.add(Arrays.asList(i, blank));
                }
            }
        }

        for(int i = 0; i < words.length; i++) {
            String reversed = reverse(words[i]);
            if(map.containsKey(reversed) && map.get(reversed) != i) {
                result.add(Arrays.asList(i, map.get(reversed)));
            }
        }

        for(int i = 0; i < words.length; i++) {
            String curr = words[i];
            for(int cut = 1; cut < curr.length(); cut++) {
                if(isPalindrome(curr.substring(cut))) {
                    String reversed = reverse(curr.substring(0,cut));
                    if(map.containsKey(reversed) && map.get(reversed) != i) {
                        result.add(Arrays.asList(i, map.get(reversed)));
                    }
                }
                if(isPalindrome(curr.substring(0,cut))) {
                    String reversed = reverse(curr.substring(cut));
                    if(map.containsKey(reversed) && map.get(reversed) != i) {
                        result.add(Arrays.asList(map.get(reversed), i));
                    }
                }
            }
        }
        return result;
    }

    private boolean isPalindrome(String str) {
        int left = 0;
        int right = str.length() - 1;
        while(left <= right) {
            if(str.charAt(left) != str.charAt(right)) return false;
            left++;
            right--;
        }
        return true;
    }

    private String reverse(String str) {
        StringBuilder sb = new StringBuilder(str).reverse();
        return sb.toString();
    }
}
