class Solution:
    def palindromePairs(self, words: List[str]) -> List[List[int]]:
		# check if a string is palindrome
        def isPalin(string):
            for i in range(len(string) // 2):
                if(string[i] != string[-(i+1)]):
                    return False
            return True

        ht = {}
		# put the reversed words into the hashtable
        for idx, word in enumerate(words):
            ht[word[::-1]] = idx
        res = []

		# traverse through the words again
        for idx, word in enumerate(words):
            l = len(word)
            for i in range(l):
				# divide into left and right part
                left = word[:i]
                right = word[i:]
                if left in ht and isPalin(right) and ht[left] != idx:    
                    res.append([idx, ht[left]])
					# in case of ["a", ""]
                    if left == "":
                        res.append([ht[left], idx])
                if right in ht and isPalin(left) and ht[right] != idx:
                    res.append([ht[right], idx])  
					# in case of ["", "a"]  
                    if right == "":
                        res.append([idx, ht[right]])
        return res
