class Solution:
    def palindromePairs(self, words: List[str]) -> List[List[int]]:
        def judge(s):
            i, j = 0, len(s) - 1
            while i < j:
                if s[i] == s[j]: 
                    i += 1
                    j -= 1
                else: return False
            return True
        d = {s : i for i, s in enumerate(words)}
        res = set()
        for i, s in enumerate(words):
            for j in range(len(s) + 1):
                s1, s2 = s[:j], s[j:]
                if s1[::-1] in d and judge(s2) and d[s1[::-1]] != i:
                    res.add((i, d[s1[::-1]]))
                if s2[::-1] in d and judge(s1) and d[s2[::-1]] != i:
                    res.add((d[s2[::-1]], i))
        return list(res)
